package practice;

public class LearnIfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int maxRange= 20;
		for (int a=10;a<=maxRange;a++)
		{
			if(a%2==0)
			{
				System.out.println("even number is :" +a);
			}
			else
			{
				System.out.println("odd number is :" +a);
				
			}
	}
		}
}

	
		


